import React from "react";
import { useSelector } from "react-redux";
import Button from "./Button";
import { useState, useEffect } from "react";
import Header from "../views/UtilPage/Header";

export default function Comment() {
  const user = useSelector((state) => state.userSlice.userName);
  const [comment, setComment] = useState("");

  function commentChange(e) {
    setComment(e.target.value);
  }
  return (
    <AuthModalContext.Provider
      value={{ show: showAuthModal, setShow: setShowModal }}
    >
      <UserContext.Provider value={user}>
        <div>
          <div>
            {user && <div>Comment as {user}</div>}
            <form>
              <textarea
                className="w-full "
                rows={5}
                placeholder="   What are your thoughts?"
                onChange={commentChange}
              >
                {comment}
              </textarea>
              <div className="text-right">
                <Button className="mt-2 bg-gray-500 text-gray-300">
                  Comment
                </Button>
              </div>
            </form>
          </div>
        </div>
      </UserContext.Provider>
    </AuthModalContext.Provider>
  );
}
