import {
  configureStore,
  createSlice,
  getDefaultMiddleware,
} from "@reduxjs/toolkit";

// export const comm = createAsyncThunk("auth/login", async (loginData) => {
//   const response = await axios.post(`${API_URL}/v1/login`, loginData);
//   return response.data.data;
// });

const community = createSlice({
  name: "community",
  initialState: [],
  reducers: {
    getCommunity: (state, action) => {},
  },
  extraReducers: (builder) => {
    builder.addCase(comm.pending, (state)=>{
      state.status
    });
  },
});

// const topic = createSlice({

// });

// let stock = createSlice({
//   name: "stock",
//   initialState: [10, 11, 12],
// });

export default configureStore({
  reducer: {},
  middleware: (getDefaultMiddleware) =>
    getDefaultMiddleware({
      serializableCheck: false,
    }),
});
