import React, { useEffect } from "react";
import TimeAgo from "timeago-react";
import ReactMarkdown from "react-markdown";
import gfm from "remark-gfm";
import { useDispatch, useSelector } from "react-redux";

export default function PostContent(props) {
  const dispatch = useDispatch();
  const post_list = useSelector((state) => state.postSlice.list);
  useEffect(() => {
    dispatch(getPost(-1));
  }, []);
  return (
    <div>
      <h5 className="text-reddit_text-darker text-sm mb-1">
        {" "}
        Posted by u/{props.author} <TimeAgo datetime={props.postedAt} />
      </h5>
      <h2 className="text-xl mb-3">{props.title}</h2>
      <div className="text-sm leading-6">
        <ReactMarkdown plugins={[gfm]} children={props.body} />
      </div>
    </div>
  );
}
