import React, { useEffect, useState } from "react";
import axios from "axios";

export default function Sidemenu() {
  const [topic, setTopic] = useState([{ topic_id: 0, topic_name: "" }]);
  const onChangeTopic = (e) => setTopic([...topic, e.target.value]);

  const [sub, setSub] = useState([]);
  const onChangeSub = (e) => setSub(e.target.value);

  useEffect(() => {
    axios
      .get("/api/topic")
      .then((res) => {
        for(let i = 0; i<res.data.length;i++){
            setTopic(res.data)
        }
      })
      .catch((err) => console.log(err));
  }, []);

  useEffect(() => {
    axios
      .get("/api/sub")
      .then((res) => setSub(res.data))
      .catch((err) => console.log(err));
  }, []);

  return <div>{/* <p>{topic[0].topic_name}</p> */}</div>;
}
