import React from "react";

export default function Button(props) {
  let classNames =
    "border border-gray-300 text-dark rounded-full px-3 text-sm font-bold ";
  if (props.outline) {
    classNames += "";
  } else {
    classNames += "bg-reddit_gray";
  }
  return <button {...props} className={defaultClassnames + props.className} />;
}
