import React, { useEffect, useState } from "react";
import axios from "axios";

export default function Sidemenu() {
  const [topic, setTopic] = useState([{topic_id}]);
  const onChangeTopic = (e) => setTopic(e.target.value);

  const [sub, setSub] = useState([]);
  const onChangeSub = (e) => setSub(e.target.value);

  useEffect(() => {
    axios
      .get("/api/topic")
      .then((res) => setTopic((topic) => [...topic, res.data]))
      .catch((err) => console.log(err));
  }, []);

  useEffect(() => {
    axios
      .get("/api/sub")
      .then((res) => setSub((sub) => [...sub, res.data]))
      .catch((err) => console.log(err));
  }, []);

  return (
    <div>
      {/* <p>{topic[0].topic_name}</p>
      <p>{sub[0].sub_name}</p> */}
      <p>{topic[0].topic_name}</p>
    </div>
  );
}
