import {
  configureStore,
  createAsyncThunk,
  createSlice,
} from "@reduxjs/toolkit";
import axios from "axios";

export const action = {
  getUser: createAsyncThunk("GET/USER", async () => {
    return axios({
      method: "get",
      url: "/api/hello",
    }).then((response) => console.log(response.data));
  }),
};

let user = createSlice({
  name: "user",
  initialState: "you-eunkyung",
});

export default configureStore({
  reducer: {
    user: user.reducer,
  },
});
