import React, { useEffect, useState } from "react";
import axios from "axios";
import styles from "./Sidemenu.module.css";
import { useDispatch, useSelector } from "react-redux";
import { asyncTopic, topicSlice } from "../../../store/store";

export default function Sidemenu() {
  const topics = [
    { topic_id: 0, topic_Name: "A" },
    { topic_id: 1, topic_Name: "B" },
    { topic_id: 2, topic_Name: "C" },
    { topic_id: 3, topic_Name: "D" },
    { topic_id: 4, topic_Name: "E" },
    { topic_id: 5, topic_Name: "F" },
  ];

  const subs = [
    { sub_id: 0, sub_name: "a", topic_id: 0 },
    { sub_id: 1, sub_name: "aa", topic_id: 0 },
    { sub_id: 2, sub_name: "aaa", topic_id: 0 },
    { sub_id: 3, sub_name: "b", topic_id: 1 },
    { sub_id: 4, sub_name: "bb", topic_id: 1 },
    { sub_id: 5, sub_name: "bbb", topic_id: 1 },
    { sub_id: 6, sub_name: "c", topic_id: 2 },
    { sub_id: 7, sub_name: "cc", topic_id: 2 },
    { sub_id: 8, sub_name: "ccc", topic_id: 2 },
    { sub_id: 9, sub_name: "d", topic_id: 3 },
    { sub_id: 10, sub_name: "dd", topic_id: 3 },
    { sub_id: 11, sub_name: "ddd", topic_id: 3 },
    { sub_id: 12, sub_name: "e", topic_id: 4 },
    { sub_id: 13, sub_name: "ee", topic_id: 4 },
    { sub_id: 14, sub_name: "eee", topic_id: 4 },
    { sub_id: 15, sub_name: "f", topic_id: 5 },
    { sub_id: 16, sub_name: "ff", topic_id: 5 },
    { sub_id: 17, sub_name: "fff", topic_id: 5 },
  ];
  const subList = subs.filter((item) => topics.topic_id === subs.topic_id);

  const topicList = topics.map((topic) => <li>{topic.topic_Name}</li>);

  const dispatch = useDispatch();
  const topic = useSelector((state) => {
    return state.communitySlice.value;
  });
  const status = useSelector((state) => {
    return state.communitySlice.status;
  });
  const err = useSelector((state) => {
    return state.communitySlice.error;
  });

  useEffect(() => {
    dispatch(asyncTopic());
  }, []);

  // const [topic, setTopic] = useState({ topic: [] });
  // const onChangeTopic = (e) => {
  //   setTopic(e.target.value);
  // };

  // const [sub, setSub] = useState([]);
  // const onChangeSub = (e) => setSub(e.target.value);

  // useEffect(() => {
  //   axios
  //     .get("/api/topic")
  //     .then((res) => setTopic(res.data))
  //     .catch((err) => console.log(err));
  //   // .then((res) => setTopic((topic) => [...topic, res.data]))
  // }, []);

  // useEffect(() => {
  //   axios
  //     .get("/api/sub")
  //     .then((res) => setSub(res.data))
  //     .catch((err) => console.log(err));
  // }, []);

  return (
    <div className={styles.sidebar}>
      <p>ABC</p>
      <div className="sidebarWrapper">
        <div className={styles.sidebarMenu}>
          <div>
            <i class="fa-regular fa-circle-arrow-up-right"></i>
            <ul>
              FEEDS
              <li className="sidebarListItem">
                <i className="fa-solid fa-house"></i>Home
              </li>
              <li className="sidebarListItem">
                <span className="material-symbols-outlined">outbound</span>
                Popular
              </li>
            </ul>
          </div>
          <ul>
            RECENT
            <li>r/running</li>
          </ul>
          <ul>
            TOPICS
            {topicList}
          </ul>
        </div>
      </div>
    </div>
  );
}
