import {
  configureStore,
  createAsyncThunk,
  createReducer,
  createSlice,
  isFulfilled,
  isRejectedWithValue,
} from "@reduxjs/toolkit";
import axios from "axios";

export const action = {
  getEmployees: createAsyncThunk("GET/EMPLOYEES", async () => {
    return axios({
      method: "get",
      url: "/api/hello",
    }).then((response) => console.log(response.data))
    .catch(console.error(e))
  }),
};

const initialState = {
  employees: [],
};

export const reducer = {
  getEmployees: (state, action) => {
    state.getEmployees = action.playload.data;
  },
};

const employeesReducer = createReducer(initialState, (builder) => {
  builder.addCase(action.getEmployees, isFulfilled, reducer.getEmployees);
});

export default employeesReducer;
