import { configureStore, createSlice } from "@reduxjs/toolkit";

let user = createSlice({
  name: "user",
  initialState: "you-eunkyung",
  reducers: {
    setUser(state) {
      return "gyujin - " + state;
    },
  },
});

let regMsg = createSlice({
  name:"regMsg",
  initialState: "",
  reducers:{
    setMsg: (
      state,
      ac
    )
  },
});


export let { setUser } = user.actions;
export let {setMsg} = regMsg.actions;

let stock = createSlice({
  name: "stock",
  initialState: [10, 11, 12],
});

export default configureStore({
  reducer: {
    user: user.reducer,
    stock: stock.reducer,
  },
});
