import Header from "./Header";
import { useDispatch, useSelector } from "react-redux";
import axios from "axios";
import { React, useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import Input from "./Input";
import Textarea from "./Textarea";
import Button from "../../Layout/Button";
import { asyncSub, getPost } from "../../../store/store";
import AuthModal from "./AuthModal";
import AuthModalContext from "./AuthModalContext";
import UserContext from "../../UserContext";
import { Uploader } from "uploader"; // Installed by "react-uploader".
import { UploadButton, UploadDropzone } from "react-uploader";

// --------------------
// Create a dropzone...
// --------------------

// const MyDropzone = ({ setFiles }) => (
//   <UploadDropzone
//     uploader={uploader}
//     options={uploaderOptions}
//     onUpdate={setFiles}
//     width="600px"
//     height="375px"
//   />
// );

export default function CreatePost() {
  const [file, setFile] = useState({});
  const [files, setFiles] = useState([]);
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const [showAuthModal, setShowModal] = useState(false);
  const [user, setUser] = useState({});
  const [post_title, setPostTitle] = useState("");
  const [post_content, setPostContent] = useState("");
  const [post_imgpath, setPostImgPath] = useState("");
  const [post_link, setPostLink] = useState("");
  const [sub_id, setSubID] = useState(-1);
  const [value, setValue] = useState("default");

  const sub_list = useSelector((state) => state.subSlice.list);

  const [t, setTab] = useState(0);

  const uploader = Uploader({ apiKey: "free" });
  const uploaderOptions = {
    multi: false,
    styles: {
      colors: {
        primary: "#377dff",
      },
    },
  };

  function setImages(files) {
    files.map((file) => {
      setPostImgPath(file.filePath);
      file.fileUrl.join("\n");
    });
  }

  const MyDropzone = () => (
    <UploadDropzone
      uploader={uploader}
      options={uploaderOptions}
      // onUpdate={(files) => files.map((x) => x.fileUrl).join("\n")}
      onUpdate={console.log("aa")}
      width="600px"
      height="375px"
    />
  );
  // -----------------------------
  // Display the uploaded files...
  // -----------------------------

  const MyUploadedFiles = ({ files }) =>
    files.map((file) => {
      // Tip: save 'filePath' to your DB (not 'fileUrl').
      const filePath = file.filePath;
      const fileUrl = uploader.url(filePath, "thumbnail"); // "raw" for un-transformed file.
      return (
        <p key={fileUrl}>
          <a href={fileUrl} target="_blank">
            {fileUrl}
          </a>
        </p>
      );
    });

  // const MyDropzone = () => (
  //   <UploadDropzone
  //     uploader={uploader}
  //     options={uploaderOptions}
  //     // onUpdate={(files) => files.map((x) => x.fileUrl).join("\n")}
  //     onUpdate={(files) => files.map((x) => setPostImgPath(x.filePath))}
  //     width="600px"
  //     height="375px"
  //   />
  // );
  // // -----------------------------
  // // Display the uploaded files...
  // // -----------------------------

  // const MyUploadedFiles = ({ files }) =>
  //   files.map((file) => {
  //     // Tip: save 'filePath' to your DB (not 'fileUrl').
  //     const filePath = file.filePath;
  //     const fileUrl = uploader.url(filePath, "thumbnail"); // "raw" for un-transformed file.
  //     return (
  //       <p key={fileUrl}>
  //         <a href={fileUrl} target="_blank">
  //           {fileUrl}
  //         </a>
  //       </p>
  //     );
  //   });

  const Tab = [
    {
      title: "Post",
      index: 0,
    },
    {
      title: "Images & Video",
      index: 1,
    },
    {
      title: "Link",
      index: 2,
    },
  ];

  useEffect(() => {
    dispatch(asyncSub());
  }, []);

  const changePost = (subid) => {
    dispatch(getPost(subid));
  };

  const onPostTitleHandler = (event) => {
    setPostTitle(event.currentTarget.value);
  };

  const onPostContentHandler = (event) => {
    setPostContent(event.currentTarget.value);
  };
  const onPostLinkHandler = (event) => {
    setPostLink(event.currentTarget.value);
  };
  const onSubIDHandler = (event) => {
    setSubID(event.currentTarget.value);
  };

  function writePost(e) {
    let body = {
      post_title: post_title,
      post_content: post_content,
      post_imgpath: post_imgpath,
      post_link: post_link,
      sub_id: sub_id,
    };

    axios.post("/api/write", body).then((res) => {
      if (res.data.result === "ok") {
        navigate("/");
      } else if (res.data.result === "subError") {
        alert("커뮤니티를 설정해주세요");
      }

      console.log(res.data);
    });
  }

  return (
    <AuthModalContext.Provider
      value={{ show: showAuthModal, setShow: setShowModal }}
    >
      <UserContext.Provider value={user}>
        <div>
          <Header />
          <div className="w-screen h-screen fixed left-0 bg-reddit_gray ">
            <AuthModal />
            <div className=" p-5 self-center mx-auto rounded-md">
              <div className=" p-5 self-center mx-auto rounded-md">
                <h1 className="text-xl mb-4">Create a post</h1>

                <select
                  defaultValue={value}
                  className="h-8 w-2/5 border border-gray-300 bg-white rounded-md"
                  onChange={onSubIDHandler}
                >
                  <option value="default" disabled hidden>
                    choose a community!
                  </option>
                  {sub_list.map((sub, i) => {
                    return (
                      <option key={i} value={sub.sub_id}>
                        {sub.sub_name}
                      </option>
                    );
                  })}
                </select>

                <section>
                  {Tab.map((e, index) => (
                    <Button key={index} onClick={(e) => setTab(index)}>
                      {e.title}
                    </Button>
                  ))}

                  <div className="bg-white my-2 px-8 py-4">
                    <Input
                      className="w-full  border border-gray"
                      value={post_title}
                      type="text"
                      placeholder="title"
                      onChange={onPostTitleHandler}
                    />
                    {t === 0 && (
                      <Textarea
                        className={"w-full mb-3"}
                        placeholder="text(optional)"
                        rows={10}
                        value={post_content}
                        onChange={onPostContentHandler}
                      />
                    )}
                    {t === 1 && (
                      <div className={"w-full mb-3"} rows={10}>
                        {files.length > 0 ? (
                          <MyUploadedFiles files={files} />
                        ) : (
                          <MyDropzone setFiles={setFiles} />
                        )}
                      </div>
                    )}
                    {t === 2 && (
                      <Textarea
                        className={"w-full mb-3"}
                        placeholder="Link 받기"
                        rows={10}
                        value={post_link}
                        onChange={onPostLinkHandler}
                      />
                    )}
                    <div className={"text-right"}>
                      <Button className={"px-4 py-2 mr-3"}>Cancel</Button>
                      <Button type="submit" onClick={(e) => writePost()}>
                        Submit
                      </Button>
                    </div>
                  </div>
                </section>
              </div>
            </div>
          </div>
        </div>
      </UserContext.Provider>
    </AuthModalContext.Provider>
    // <Box w="100%" m="auto" className=" bg-reddit_gray">
    //   <Header />
    //   <Box w={["100%", "90%", "80%", "70%"]} m="auto">
    //     <form>
    //       Create a post
    //       <Stack spacing={3}>
    //         <FormControl>
    //           <RadioGroup>
    //             <Stack direction="row" spacing={3}>
    //               <Radio value="text">Post</Radio>
    //               <Radio value="link">Link</Radio>
    //             </Stack>
    //           </RadioGroup>
    //         </FormControl>
    //         <FormControl>
    //           <Input
    //             value={post_title}
    //             type="text"
    //             placeholder="title"
    //             onChange={onPostTitleHandler}
    //           />
    //         </FormControl>
    //         <FormControl>
    //           <Textarea
    //             placeholder="text(optional)"
    //             rows={10}
    //             value={post_content}
    //             onChange={onPostContentHandler}
    //           />
    //         </FormControl>
    //         <Button type="submit" onClick={(e) => writePost()}>
    //           Submit
    //         </Button>
    //       </Stack>
    //     </form>
    //   </Box>
    // </Box>
  );
}
