import React, { useEffect, useState } from "react";
import axios from "axios";
import styles from "./Sidemenu.module.css";
import { useDispatch, useSelector } from "react-redux";
import { asyncTopic, topicSlice, subSlice } from "../../../store/store";

export default function Sidemenu() {
  const topic_list = useSelector((state) => state.topicSlice.list);
  const dispatch = useDispatch();
  useEffect(() => {
    dispatch(asyncTopic());
  }, []);

  const sub_list = useSelector((state) => state.subSlice.list);
  useEffect(() => {
    dispatch(asyncTopic());
  }, []);

  // // const [sub, setSub] = useState([]);
  // // const onChangeSub = (e) => setSub(e.target.value);

  // useEffect(() => {
  //   axios
  //     .get("/api/topic")
  //     .then((res) => setTopic(res.data))
  //     .catch((err) => console.log(err));
  //   // .then((res) => setTopic((topic) => [...topic, res.data]))
  // }, []);

  // useEffect(() => {
  //   axios
  //     .get("/api/sub")
  //     .then((res) => setSub(res.data))
  //     .catch((err) => console.log(err));
  // }, []);

  return (
    <div className={styles.sidebar}>
      <div className="sidebarWrapper">
        <div className={styles.sidebarMenu}>
          <div>
            <i className="fa-regular fa-circle-arrow-up-right"></i>
            <ul>
              FEEDS
              <li className="sidebarListItem">
                <i className="fa-solid fa-house"></i>Home
              </li>
              <li className="sidebarListItem">
                <span className="material-symbols-outlined">outbound</span>
                Popular
              </li>
            </ul>
          </div>
          <ul>
            RECENT
            <li>r/running</li>
          </ul>
          <ul>
            TOPICS
            {/* {topic[4].topic_name} */}
            {/* {topicList} */}
          </ul>
          {topic_list.map((item, index) => (
            <div key={index}>{item.topic_name}</div>
          ))}
        </div>
      </div>
    </div>
  );
}
