import { ChatBubbleLeftIcon } from "@heroicons/react/24/outline";
import { React, useState, useEffect } from "react";
import Comment from "./Comment";
import TimeAgo from "timeago-react";
import Header from "../views/UtilPage/Header";
import { useLocation } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";
import { asyncComment } from "../../store/store";
import Button from "./Button";
import {
  HandThumbDownIcon,
  HandThumbUpIcon,
} from "@heroicons/react/24/outline";
import Avatar from "../../img/avatar.png";

export default function PostPage() {
  const dispatch = useDispatch();
  const { state } = useLocation();

  const user = useSelector((state) => state.userSlice.userName);
  // 댓글 기능
  const [comment, setComment] = useState("");
  const comment_list = useSelector((state) => state.commentSlice.list);
  const [isValid, SetIsValid] = useState(false);

  function commentChange(e) {
    setComment(e.target.value);
  }

  useEffect(() => {
    console.log(state.post_id);
    dispatch(asyncComment(state.post_id));
    console.log(comment_list);
  }, []);

  const commentFeed = (props) => {
    return (
      <div>
        <div>
          <p>{props.user_name}</p>
          <div>{props.comment_content}</div>
        </div>
        <HandThumbUpIcon /> <HandThumbDownIcon />
      </div>
    );
  };

  return (
    <div>
      <Header />
      <div className="bg-reddit_gray px-4 py-3">
        <div className="  w-5/6 m-auto p-6 bg-white">
          <div className="border border-gray-300 bg-white p-2 pb-0 my-3 rounded-md">
            {/* <PostContent /> */}
            <div className=" flex mb-1">
              <p className="text-reddit_text text-sm ">
                • Posted by u/ {state.user_name}
                <TimeAgo datetime={state.post_date} />
              </p>
            </div>
            <h2 className="text-xl mb-3"> {state.post_title}</h2>
            <div className="text-sm leading-6 text-gray-700 ">
              <p>{state.post_content}</p>
            </div>
            <nav className="flex">
              <div className="text-center float-left	">
                <button className="flex-auto">
                  <ChatBubbleLeftIcon className="text-gray-400 w-5 h-5 mt-4 m-1" />
                </button>
              </div>
              <div className="text-center float-left text-sm text-gray-400 mt-3.5	">
                Comments
              </div>
            </nav>
          </div>
          <div>
            <div>
              {user && <div>Comment as {user}</div>}
              <form className="border border-gray-300 ">
                <textarea
                  className="w-full "
                  rows={5}
                  placeholder="   What are your thoughts?"
                  onChange={commentChange}
                  // onKeyUp={(e) => {
                  //   e.target.value.length > 0
                  //     ? SetIsValid(true)
                  //     : SetIsValid(false);
                  // }}
                  value={comment}
                ></textarea>
              </form>
              <div className="text-right">
                <Button
                  className="h-5 mt-2 bg-gray-500 text-gray-300"
                  disabled={isValid ? false : true}
                >
                  Comment
                </Button>
              </div>
            </div>
          </div>
          {comment_list.map((comment, i) => {
            var [edit, setEdit] = useState(false);

            function clickEvent() {
              edit = true;
            }
            return (
              <div className="py-3 space-y-2">
                <div className="flex space-x-2">
                  {" "}
                  <div className=" w-7 h-7 bg-gray-400 rounded-full">
                    <img
                      src={Avatar}
                      alt=""
                      style={{ filter: "invert(100%)" }}
                      className="block"
                    />
                  </div>
                  <p key={i}>{comment.user_name}</p>
                  <TimeAgo
                    className="text-gray-400"
                    datetime={comment.comment_date}
                  />
                </div>
                {edit === false ? <p>abcdefg</p> : <p>123456</p>}
                <p key={i}>{comment.comment_content}</p>
                <div className="flex space-x-2  text-gray-500">
                  <HandThumbUpIcon className="h-5 w-5 " />
                  <HandThumbDownIcon className="h-5 w-5" />
                  <button onClick={() => clickEvent()}>Edit</button>
                  <button>Delete</button>
                </div>
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
}
