import React, { useContext, useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { asyncVoteSum, getPost } from "../../store/store";
import PostContent from "../views/UtilPage/PostContent";
import TimeAgo from "timeago-react";
import PostForm from "./PostForm";
import {
  ChatBubbleLeftIcon,
  HandThumbDownIcon,
  HandThumbUpIcon,
} from "@heroicons/react/24/outline";
import CommentModal from "./CommentModal";
import { useNavigate } from "react-router-dom";
export default function Post(props) {
  const dispatch = useDispatch();
  const navigate = useNavigate();

  const user = useSelector((state) => state.userSlice.userName);

  const sub_list = useSelector((state) => state.subSlice.list);

  const post_list = useSelector((state) => state.postSlice.list);

  const voteSum = useSelector((state) => state.voteSumSlice.list);
  useEffect(() => {
    dispatch(getPost(-1));
    dispatch(asyncVoteSum());
  }, []);

  const commentModal = useContext(CommentModal);

  return (
    <div>
      <PostForm />
      <div className=" w-full y-screen px-6 bg-reddit_gray">
        {post_list.map((item, index) => (
          <div
            className="border border-gray-300 bg-white my-3 rounded-md flex"
            key={index}
          >
            <div className="float-left bg-gray-100 p-2 text-center space-y-1 ">
              <HandThumbUpIcon className="h-4 text-gray-500" />
              {voteSum.map((vote, i) => {
                if (vote.post_id === item.post_id) {
                  return (
                    <MenuItem
                      onClick={() => {
                        changePost(sub.sub_id);
                      }}
                      key={j}
                    >
                      {sub.sub_name}
                    </MenuItem>
                  );
                }
              })}
              <HandThumbDownIcon className="text-gray-500" />
            </div>
            <div className="  p-2">
              {/* <PostContent /> */}
              <div className=" flex mb-1">
                {sub_list.map((sub, j) => {
                  if (item.sub_id === sub.sub_id) {
                    return <h5 key={j}>{sub.sub_name}</h5>;
                  }
                })}
                <p className="text-reddit_text text-sm ">
                  â€¢ Posted by u/{item.user_name}
                  <TimeAgo datetime={item.post_date} />
                </p>
              </div>
              <h2 className="text-xl mb-3"> {item.post_title}</h2>
              <div className="text-sm leading-6 text-gray-700">
                <p>{item.post_content}</p>
              </div>
              <nav className="flex">
                <div
                  className="text-center float-left	flex"
                  onClick={() => {
                    navigate("/PostPage", { state: item });
                  }}
                >
                  <button className="flex-auto">
                    <ChatBubbleLeftIcon className="text-gray-400 w-5 h-5 m-1" />
                  </button>
                  <p className="px-1">1</p>
                  <p>comments</p>
                </div>
                {/* <div className="text-center float-left text-sm text-gray-400 mt-3.5 flex	">
                  <p>1</p> Comments
                </div> */}
              </nav>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
