import React, { useEffect } from "react";
import { Sidebar, Menu, MenuItem, SubMenu } from "react-pro-sidebar";
import { useDispatch, useSelector } from "react-redux";
import { asyncTopic, asyncSub } from "../../../store/store";

export default function ExampleSide() {
  const dispatch = useDispatch();

  const topic_list = useSelector((state) => state.topicSlice.list);
  const sub_list = useSelector((state) => state.subSlice.list);
  useEffect(() => {
    dispatch(asyncTopic());
    dispatch(asyncSub());
  }, []);

  //   return (
  //     <div>
  //       <Sidebar>
  //         <Menu>
  //           {topic_list.map((tp, i) => {
  //             if (1 === 1) {
  //               return <MenuItem>Dashboard</MenuItem>;
  //             }
  //           })}
  //         </Menu>
  //       </Sidebar>
  //     </div>
  //   );

  return (
    <div>
      <Sidebar>
        <Menu>
          {topic_list.map((tp, i) => (
            <SubMenu key={i} label={tp.topic_name}>
              {sub_list.map((sub, j) => {
                if(tp.topic_id === sub.topic_)
                <MenuItem key={j}>{sub.sub_name}</MenuItem>;
              })}
            </SubMenu>
          ))}
        </Menu>
      </Sidebar>
    </div>
  );
}
