import {
  configureStore,
  createAsyncThunk,
  createSlice,
  getDefaultMiddleware,
  isRejectedWithValue,
} from "@reduxjs/toolkit";
import axios from "axios";

// export const comm = createAsyncThunk("auth/login", async (loginData) => {
//   const response = await axios.post(`${API_URL}/v1/login`, loginData);
//   return response.data.data;
// });

let body = {
  name: "abc",
  password: "1234",
};

const asyncCommunity = createAsyncThunk(
  "communitySlice/asyncCommunity",
  async () => {
    const resp = await axios.post("/api/login", body);
    // const data = await resp.data.json().body;
    return resp.data;
  }
);

