//   name: "communitySlice",
//   initialState: {
//     value: 0,
//     status: "Welcome",
//   },
//   reducers: {
//     getCommunity: (state, action) => {
//       state.value = state.value + action.payload;
//     },
//   },
// });

const communitySlice = createSlice({
  name: "communitySlice",
  initialState: {
    value: {},
    status: "Welcome",
    error: null,
  },
  extraReducers: (builder) => {
    builder.addCase(asyncCommunity.pending, (state, action) => {
      state.status = "Loading";
    });
    builder.addCase(asyncCommunity.fulfilled, (state, action) => {
      state.value = action.payload;
      state.status = "complete";
    });
    builder.addCase(asyncCommunity.rejected, (state, action) => {
      state.error = action.payload;
      state.status = "fail";
    });
  },
});

