import {
  configureStore,
  createAsyncThunk,
  createSlice,
} from "@reduxjs/toolkit";
import axios from "axios";

let body = {
  name: "abc",
  password: "1234",
};

const asyncCommunity = createAsyncThunk(
  "communitySlice/asyncCommunity",
  async () => {
    const resp = await axios.post("/api/login", body);
    return resp.data.result;
  }
);

const communitySlice = createSlice({
  name: "communitySlice",
  initialState: {
    value: "",
    status: "Welcome",
    error: null,
  },
  extraReducers: (builder) => {
    builder.addCase(asyncCommunity.pending, (state, action) => {
      state.status = "Loading";
    });
    builder.addCase(asyncCommunity.fulfilled, (state, action) => {
      state.value = action.payload;
      state.status = "complete";
    });
    builder.addCase(asyncCommunity.rejected, (state, action) => {
      state.error = action.payload;
      state.status = "fail";
    });
  },
});

export default configureStore({
  reducer: {
    communitySlice: communitySlice.reducer,
  },
});

export { asyncCommunity };
