import React, { useEffect, useState } from "react";
import axios from "axios";

export default function Sidemenu() {
  const [topic, setTopic] = useState({ topic_id: 0, topic_name: "" });
  const [topicList, setTopics] = useState([]);
  const onChangeTopic = (e) => {
    setTopic(e.target.value);
  };

  const [sub, setSub] = useState([]);
  const onChangeSub = (e) => setSub(e.target.value);

  useEffect(() => {
    axios
      .get("/api/topic")
      .then((res) => {
        for (let i = 0; i < res.data.length; i++) {
          setTopic(res.data[i]);
          topicList.push({
            topic_id: res.data[i].topic_id,
            topic_name: res.data[i].topic_name,
          });
        }
      })
      .catch((err) => console.log(err));
  }, []);

  useEffect(() => {
    axios
      .get("/api/sub")
      .then((res) => setSub(res.data))
      .catch((err) => console.log(err));
  }, []);

  return (
    <div>
      <p>{topicList[0].topic_name}</p>
    </div>
  );
}
