import { useEffect, useRef, useState } from "react";

const useDetectClose = () => {
  const [userDropdownVisibilityClass, setUserDropdownVisibilityClass] =
    useState("false");
  const ref = useRef(null);

  const removeHandeler = () => {
    setUserDropdownVisibilityClass(!userDropdownVisibilityClass);
  };

  useEffect(() => {
    const onClick = (e) => {
      if (ref.current !== null && !ref.current.contains(e.target)) {
        //드롭다운 메뉴 이외의 공간 클릭
        setUserDropdownVisibilityClass(!userDropdownVisibilityClass);
      }
    };

    if (userDropdownVisibilityClass) {
      window.addEventListener("click", onClick);
    }

    return () => {
      window.removeEventListener("click", onClick);
    };
  }, [userDropdownVisibilityClass]);

  return [userDropdownVisibilityClass, ref, removeHandeler];
};

export default useDetectClose;
